mapper.select:
select
SEQ,NAME,TASK,JOB_GRADE,OUTSIDE_DATE,START_TIME,END_TIME,START_PLACE,END_PLACE,STATE_YSN, APPLICATION_TIME,NOTE,REPLY,REG_DT,MOD_DT,MONTH,DAY
from
(select OSJ.SEQ,INFO.NAME,INFO.TASK,INFO.JOB_GRADE,OSJ.OUTSIDE_DATE,OSJ.START_TIME,OSJ.END_TIME,OSJ.START_PLACE,OSJ.END_PLACE,OSJ.STATE_YSN,OSJ.APPLICATION_TIME,OSJ.NOTE,OSJ.REPLY,OSJ.REG_DT,OSJ.MOD_DT,OSJ.MONTH,OSJ.DAY,row_number() over(order by


        OSJ.${sortKeyword}


        INFO.${sortKeyword}

 ${sort})
as rNum from HOLIDAY_INFO_TB INFO,OUTSIDEJOB_TB OSJ where INFO.SEQ = OSJ.USER_NUM AND ) where rNum between #{rowStart} and #{rowEnd} order by ${sortKeyword} ${sort}
